package com.example.androidproject;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import com.example.androidproject.adapter.CategoryAdapter;
import com.example.androidproject.model.CategoryModel;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    RecyclerView recyclerView;
    CategoryAdapter categoryAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        List<CategoryModel> categories = new ArrayList<>();
        categories.add(new CategoryModel("","Mathematics","data:image/png;base64,"));
        categories.add(new CategoryModel("","Science","data:image/png;base64,"));
        categories.add(new CategoryModel("","History","data:image/png;base64,"));
        categories.add(new CategoryModel("","Language","data:image/png;base64,"));
        categories.add(new CategoryModel("","Puzzle","data:image/png;base64,"));
        categories.add(new CategoryModel("","Drama","data:image/png;base64,"));

        recyclerView = findViewById(R.id.categoryList);
        categoryAdapter = new CategoryAdapter(this, categories);
        RecyclerView.LayoutManager layoutManager = new GridLayoutManager(this, 2);
        recyclerView.setLayoutManager(layoutManager);
        recyclerView.setAdapter(categoryAdapter);
    }
}